create table about_us (id int8 generated by default as identity, description TEXT not null, imageurl varchar(255) not null, primary key (id));
create table category (id int8 generated by default as identity, name varchar(255) not null, primary key (id));
create table product (id int8 generated by default as identity, description TEXT not null, imageurl varchar(255), name varchar(255) not null, payment_terms varchar(255) not null, price float8 not null, promotional_price float8, sizes varchar(255), primary key (id));
create table product_category (product_id int8 not null, category_id int8 not null, primary key (product_id, category_id));
create table tb_user (id int8 generated by default as identity, email varchar(255) not null, password varchar(255) not null, user_name varchar(255) not null, primary key (id));
alter table if exists tb_user add constraint UK_4vih17mube9j7cqyjlfbcrk4m unique (email);
alter table if exists product_category add constraint FKkud35ls1d40wpjb5htpp14q4e foreign key (category_id) references category;
alter table if exists product_category add constraint FK2k3smhbruedlcrvu6clued06x foreign key (product_id) references product;
